import React, { Component } from 'react';
import {connect} from 'react-redux';
import Todo from '../../components/Todo/Todo';
import TodoDetail from '../../components/TodoDetail/TodoDetail';
import * as actionTypes from '../../store/actions/actionTypes';
import './TodoList.css';

import { NavLink } from 'react-router-dom';

class TodoList extends Component {
  state = {
    todos: [
      { id: 1, title: 'SWPP', content: 'take swpp class', done: true },
      { id: 2, title: 'Movie', content: 'watch movie', done: false },
      { id: 3, title: 'Dinner', content: 'eat dinner', done: false }
    ],
    selectedTodo: null,
  }

  clickTodoHandler = (td) => {
    this.props.history.push('/todos/' + td.id);
  }

  render() {
    const todos = this.props.storedTodos.map((td) => {
      return (
        <Todo
          key={td.id}
          title={td.title}
          done={td.done}
          clickDetail={() => this.clickTodoHandler(td)}
          clickDone={() => this.props.onToggleTodo(td)}
          clickDelete={() => this.props.onDeleteTodo(td)}
        />);
    });

    let todo = null;
    if (this.state.selectedTodo) {
      todo = <TodoDetail
        title={this.state.selectedTodo.title}
        content={this.state.selectedTodo.content}
      />
    }
    return (
      <div className="TodoList">
        <div className="title">{this.props.title}</div>
        <div className="todos">{todos}</div>
        {todo}
        <NavLink to='/new-todo' exact>New Todo</NavLink>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onToggleTodo: (id) =>  
      dispatch({type: actionTypes.TOGGLE_DONE, targetID: id}),
    onDeleteTodo: (id) => 
      dispatch({type: actionTypes.DELETE_TODO, targetID: id}),
  };
}

const mapStateToProps = (state) => {
  return {
    storedTodos: state.td.todos
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoList);
